import React from 'react';
import PropTypes from 'prop-types';

import Escrever from './Escrever'
import Pontos from './Pontos'

export default class CachorroBola extends React.Component{

  constructor(){
    super();
    this.state={
      palavra_escrita:"",
      palavra:"",
      palavra_na_tela:"cachorro",
      pontos:0,
      timer:0
    };

    this.tick = () => {
      this.setState({timer:this.state.timer + 1})
    }

      this.timerID = () => setInterval(
        this.tick(),
        1000
        );




  }

  render(){
    this.changePalavra = (evento) => {
      this.setState({palavra_escrita: evento.target.value})
    }

    this.muda = (evento) => {
      console.log(this.state.palavra_escrita)
      console.log(this.state.palavra_na_tela)
      this.setState({palavra_escrita: ''})
      if(this.state.palavra_escrita == this.state.palavra_na_tela){
        this.setState({pontos: this.state.pontos + 1})
      }
    }
    return(
      <div id = 'central'>
        <h1 id= 'title'>Cachorro ou Bola</h1>
        <div id = 'pontos'>
          <Pontos pontos={this.state.pontos}/>
        </div>

        <div id='palavra'>
          <h1>{this.state.palavra_na_tela} + {this.state.pontos}</h1>
          <h1>{this.state.timer}</h1>
        </div>

        <div id='chat'>
          <Escrever muda={this.muda} palavra={this.state.palavra_escrita} changePalavra={this.changePalavra}/>
        </div>
      </div>
    );
  }
}

