import React from 'react';
import PropTypes from 'prop-types';

import Escrever from './Escrever'
import Pontos from './Pontos'

export default class CachorroBola extends React.Component{

  constructor(){
    super();
    this.state={
      palavra_escrita:"",
      palavra:"",
      palavra_na_tela:"cachorro",
      pontos:0,
      timercount:5,
      jogando:1
    };

    this.componentDidMount = () =>{
      var intervalId = setInterval(this.timer,1000);
      this.setState({intervalId: intervalId});

    };



    this.timer = () => {
      if(this.state.jogando == 1){
        this.setState({timercount : this.state.timercount - 1});
        if(this.state.timercount == 0){
          this.setState({timercount: 5})
          this.mostrarPontuacao()
        }
      }
    }


    this.mostrarPontuacao = () => {
      this.setState({jogando:0})
      console.log('cheguei aqui')

    }






  }

  render(){
    this.changePalavra = (evento) => {
      this.setState({palavra_escrita: evento.target.value})
    }

    this.muda = (evento) => {
      console.log(this.state.palavra_escrita)
      console.log(this.state.palavra_na_tela)
      this.setState({palavra_escrita: ''})
      if(this.state.palavra_escrita == this.state.palavra_na_tela){
        this.setState({pontos: this.state.pontos + 1})
      }
    }
    return(
      <div id = 'central'>
        <h1 id= 'title'>Cachorro ou Bola</h1>

        <div id='tempo'>
          <h1 id='cor_tempo'>Tempo restante: {this.state.timercount}</h1>
        </div>

        <div id = 'pontos'>
          <Pontos pontos={this.state.pontos}/>
        </div>

        <div id='palavra'>
          <h1>{this.state.palavra_na_tela}</h1>
        </div>

        <div id='chat'>
          <Escrever muda={this.muda} palavra={this.state.palavra_escrita} changePalavra={this.changePalavra}/>
        </div>
      </div>
    );
  }
}

