import React from 'react';
import PropTypes from 'prop-types';

import Escrever from './Escrever'

export default class CachorroBola extends React.Component{

  constructor(){
    super();
    this.state={
      palavra_escrita:""
    }

    this.changePalavra = (evento) => {
      this.setState({palavra: evento.target.value})
    }

  }

  render(){

    return(
      <div id = 'central'>
        <h1 id= 'title'>Cachorro ou Bola</h1>
        <div id = 'pontos'></div>
        <div id='palavra'></div>

        <div id='chat'>
          <Escrever palavra_escrita={this.state.palavra_escrita}/>
        </div>
      </div>
    );
  }
}

