import React from 'react';
import PropTypes from 'prop-types';

import Escrever from './Escrever'

export default class CachorroBola extends React.Component{

  constructor(){
    super();
    this.state={
      palavra_escrita:"",
      palavra:""
    }

    this.changePalavra = (evento) => {
      this.setState({palavra_escrita: evento.target.value})
    }

    this.muda = (evento) => {
      this.setState({palavra: this.state.palavra_escrita})
    }


  }

  render(){

    return(
      <div id = 'central'>
        <h1 id= 'title'>Cachorro ou Bola</h1>
        <div id = 'pontos'></div>

        <div id='palavra'>
          <h1>{this.state.palavra_escrita}</h1>
        </div>

        <div id='chat'>
          <Escrever palavra={this.state.palavra_escrita} changePalavra={this.changePalavra}/>
        </div>
      </div>
    );
  }
}

