import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class MyButton extends Component {
  render(){
    return(
    <button
      onClick={() => {this.props.handleClick(this.props.label);}}
    >
      {this.props.label}
    </button>
    )
  }
}

class MyLabel extends Component {
  render(){
    return(
     <p>{this.props.text} : {this.props.botao}</p>
   )
  }
}

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      labelText: '',
    };

    this.setLabelText = this.setLabelText.bind(this);
  }

  setLabelText(parametro){
    this.setState({ labelText: parametro })
  }

  render(){
    console.log(this.state)
    return(
      <div className="App">
        <MyLabel text="Escrevendo pelo App" botao={this.state.labelText} />
        <MyButton handleClick={this.setLabelText} label="Bot達o 1"/>
        <MyButton handleClick={this.setLabelText} label="Bot達o 2"/>
        <MyButton handleClick={this.setLabelText} label="Bot達o 3"/>
        <MyButton handleClick={this.setLabelText} label="Bot達o 4"/>
      </div>
    )
  }
}

export default App;
