import React, {Component} from 'react';
import BotaoGol from './BotaoGol';

export default class Time extends Component{

  constructor(){
    super();
    this.state = {
      gols : 0
    }
  }

  gol(){

    this.setState({gols : this.state.gols + 1})
  }

  render(){
    return(
      <div>
        <h1>Vasco</h1>
        <h2>{this.state.gols}</h2>
        <BotaoGol mudaGol = {this.gol} />
      </div>
    );
  }

}
