import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class Contador extends Component{
  render(){
    return(
      <div>
      <h1 className="margem">0</h1>,
      <button>
      Contador
      </button>
      </div>
    );
  }
}

class MyButton extends Component {
  render(){
    return(
    <button className="button"
      onClick={() => {this.props.handleClick(this.props.label);}}
    >
      {this.props.label}
    </button>
    )
  }
}

class MyLabel extends Component {
  render(){
    return(
     <p className="margem">{this.props.text} : {this.props.botao}</p>
   )
  }
}

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      labelText: '',
    };

    this.setLabelText = this.setLabelText.bind(this);
  }

  setLabelText(parametro){
    this.setState({ labelText: parametro })
  }

  render(){
    console.log(this.state)
    return(
      <div className="App">

        <div className="App3">
          <MyLabel text="Escrevendo pelo App" botao={this.state.labelText} />
        </div>
        <div className="App4">
          <MyButton handleClick={this.setLabelText} label="Botão 1"/>
          <MyButton handleClick={this.setLabelText} label="Botão 2"/>
          <MyButton handleClick={this.setLabelText} label="Botão 3"/>
          <MyButton handleClick={this.setLabelText} label="Botão 4"/>
        </div>
        <div className="App2">
        <h1 className="h1">Olá mundo</h1>
        </div>
        <div>
          <Contador />
        </div>
      </div>

    )
  }
}

export default App;
