import React from 'react';
import PropTypes from 'prop-types';

export default class Escrever extends React.Component{
  constructor(){
    super();
    this.state={
      palavra:""
    };
  }
  render(){

    return(
      <div>
      <label id="forms">Escreva a palavra:</label><br/>


      <input
        className="input_palavra" type='text' name='texto_palavra' value={this.state.nome}
      />


      <input className="bt" type='button' value='enviar'/>
      </div>
    );
  }
}

