import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class MyButton extends Component {
  render(){
    return(
    <button
      onClick={() => {this.props.handleClick('coxinha');}}
    >
      {this.props.label}
    </button>
    )
  }
}

class MyLabel extends Component {
  render(){
    return(
     <p>{this.props.text}</p>
   )
  }
}

class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      labelText: '',
    };
  }

  setLabelText = (parametro) => {
    this.setState({ labelText: parametro })
  }

  render(){
    console.log(this.state)
    return(
      <div className="App">
        <MyLabel text="Escrevendo pelo App" />
        <MyButton handleClick={this.setLabelText} label="Bot達o 1"/>
        <MyButton handleClick={this.setLabelText} label="Bot達o 2"/>
        <MyButton handleClick={this.setLabelText} label="Bot達o 3"/>
        <MyButton handleClick={this.setLabelText} label="Bot達o 4"/>
      </div>
    )
  }
}

export default App;
